router.get("/", async (req, res) => {
  try {
    const articles = await Article.find({});
    res.status(200).send(articles);
  } catch (error) {
    res.status(404).send({ error: "Problem getting articles" });
  }
});

/**
 * @swagger
 * "/article/{articleId}":
 *   get:
 *     summary: Find article by ID
 *     tags:
 *       - Article
 *     parameters:
 *       - name: articleId
 *         in: path
 *         required: true
 *         schema:
 *           type: string
 *         description: The Id of the article
 *     responses:
 *       "200":
 *         description: successful operation
 *         content:
 *           application/json:
 *             schema:
 *               $ref: "#/components/schemas/Article"
 *       "404":
 *         description: Article not found
 */

router.get("/:id", async (req, res) => {
  try {
    const article = await Article.findOne({ _id: req.params.id });
    if (article) {
      res.status(200).send(article);
    } else {
      res.status(404).send({ error: "Article doesn't exist !" });
    }
  } catch (err) {
    res.status(404).send({ error: "Article doesn't exist !" });
    // console.log(err)
  }
});

/**
 * @swagger
 * /article:
 *   post:
 *     summary: Add New Article
 *     tags:
 *       - Article
 *     requestBody:
 *       required: true
 *       content:
 *         application/json:
 *             schema:
 *               $ref: '#/components/schemas/Article'
 *     responses:
 *       '400':
 *         description: Bad Request
 *       '201':
 *         description: Query added.
 *         content:
 *           application/json:
 *             schema:
 *               type: object
 *               properties:
 *                 Message:
 *                   type: string
 */

router.post(
  "/",
  verifyToken,
  validateMiddleware(validateArticle),
  async (req, res) => {
    // console.log(req.body)
    try {
      const newArticle = await new Article({
        heading: req.body.heading,
        content: req.body.content,
        userId: req.user["id"],
        image: req.body.image,
      });
      // console.log(req.user["id"])
      await newArticle.save();

      res.status(201).send({ Message: "New Article Created" });
    } catch (error) {
      res
        .status(400)
        .send({ error: "There was a problem publishing the article" });
      //    console.log(error)
    }
  }
);

/**
 * @swagger
 * "/article/{articleId}":
 *   delete:
 *     summary: Delete article according to ID
 *     tags:
 *       - Article
 *     parameters:
 *       - name: articleId
 *         in: path
 *         required: true
 *         schema:
 *           type: string
 *         description: The Id of the article
